INSERT INTO `knowledge_vectors` VALUES (1,'job',1,'a8e6e229-1824-40be-a1a4-e3a24b08f6aa','Vị trí: Nhân Viên Kỹ Thuật FPT Telecom - Q. Tân Bình\nCông ty: FPT Telecom\nMức lương: 10000000\nKĩ năng yêu cầu: Làm việc nhóm, Quản lý thời gian\nKinh nghiệm: ONE_TO_THREE_YEARS\nĐịa điểm: Thành phố Hồ Chí Minh\nNgành nghề: IT & Telecommunications\nMô tả: - Bảo trì, khắc phục lỗi kỹ thuật liên quan đến dịch vụ Internet, dịch vụ truyền hình FPT và các dịch vụ viễn thông tại nhà khách hàng.\n\n- Triển khai nâng cấp dịch vụ truyền hình FPT, hệ thống Wifi, Camera và các dịch vụ gia tăng khác trên đường truyền Internet tại nhà khách hàng.\n\n- Ghi nhận phản hồi trực tiếp từ khách hàng, phối hợp với các bộ phận liên quan trong việc nâng cao chất lượng dịch vụ của công ty.\n\n- Tư vấn, hướng dẫn sử dụng và cài đặt các dịch vụ đi kèm do FPT Telecom cung cấp cho khách hàng (Truyền hình FPT, Camera, thiết bị wifi...).\n\n- Hỗ trợ kỹ thuật tại nhà khách hàng.\n',NULL,'job','DONE',NULL,NULL,NULL),(2,'job',2,'d873f90b-928b-4318-a84e-ea699dbc1d96','Vị trí: Chuyên Viên Kinh Doanh Kênh Phân Phối B2C\nCông ty: FPT Telecom\nMức lương: 20000000\nKĩ năng yêu cầu: Làm việc nhóm, Thuyết trình\nKinh nghiệm: ONE_TO_THREE_YEARS\nĐịa điểm: Thành phố Hồ Chí Minh\nNgành nghề: IT & Telecommunications\nMô tả: - Sản phẩm kinh doanh chính: FPT Smart Home, FPT Camera\n- Tìm kiếm các đơn vị để phân phối các sản phẩm FPT Smart Home, FPT Camera\n- Mở rộng kênh bán hàng tại khu vực TP. Hồ Chí Minh\n- Tổ chức Workshop, bán hàng cùng Brandshop, Nhà phân phối, Đại lý\n- Tìm kiếm các đơn vị nhượng quyền của FPT Smart Home để bán hàng FPT Smart Home\n- Mở rộng đại lý cấp 2 cho các Brandshop/Nhà phân phối đang hỗ trợ\n- Mở rộng các đối tác từ kênh bán hàng: Xây dựng, camera, nội thất, vật liệu xây dựng, nhà bếp, cửa cổng, thầu xây dựng, ...\n- Làm việc tại: 336 Huỳnh Tấn Phát, Quận 7, TPHCM.\n',NULL,'job','DONE',NULL,NULL,NULL),(3,'job',3,'b90b5a2f-da26-442a-a1fb-f37c71e83f9a','Vị trí: Tuyển dụng Frontend Developer (R